        dragonballkai.child("E011").setValue(DragonBallKai);

        DragonBallKai.setEserie("Dragon Ball Kai");
        DragonBallKai.setEtitle("As Lágrimas de Piccolo! O Contra-ataque Furioso de Goku!");
        DragonBallKai.setEsinopse("Goku chega finalmente ao local onde se encontram os sayajins e facilmente dá uma surra em Nappa. \n");
        DragonBallKai.setEid("012");
